package com.library.book.domain.model.bookcopy;

import lombok.EqualsAndHashCode;
import lombok.Getter;

import java.util.UUID;

@Getter
@EqualsAndHashCode
public class BookCopyId {
    private final Long value;

    public BookCopyId(Long value) {
        if (value == null || value <= 0) {
            throw new IllegalArgumentException("BookCopy ID cannot be null or negative");
        }
        this.value = value;
    }

    public static BookCopyId createNew() {
        // In real implementation, this would be generated by database
        return new BookCopyId(Math.abs(UUID.randomUUID().getMostSignificantBits()));
    }

    @Override
    public String toString() {
        return value.toString();
    }
}